import React from 'react';
import '../App.css';
import {  useLocation } from 'react-router-dom';
import {  useParams } from 'react-router-dom';
import {useState} from 'react';

function Form() {

  // this.state = {value: 'Wartosc imienia'};
// this.handleChange =
// this.doChange.bind(target.value);
// this.handleSubmit =
// this.doSubmit.bind(target.value);

const loc = useLocation();
const { id } = useParams();
const { idx, unit, name } = useParams();
const [cnt, setCountER] = useState(0);
const [formType, setFormType] = useState('');

const handleFormTypeChange = (e) => {
  setFormType(e.target.value);
};

let formContent = null;
  if (id === '1') {
    formContent = (
      <form>
        <label>Question 1</label>
        <input type='text' />
      </form>
    );
  } else if (id === '2') {
    formContent = (
      <form>
        <label>Question 2</label>
        <input type='text' />
      </form>
    );
  }

  return (
    <div className='Form'>
      <header className='App-header'>
        {/* <p>com.Form</p>
        <p>Case ID: {id}</p>
        <p>Case URL: {loc.pathname}</p>
        <p>
          Test params: {idx} Klasa {unit} Name {name}
        </p>
        <p>Proba przeslania from: {cnt}</p> */}
        {formContent}
        <form method='post' action=''>
          <label>Imie</label>
          <input type='text' value='' />
          <br />
          <br />
          <label>Nazwisko</label>
          <input type='text' value='' />
          <br />
          <br />
          <label>Form Type</label>
          <select value={formType} onChange={handleFormTypeChange}>
            <option value=''>Select form type</option>
            <option value='form1'>Form 1</option>
            <option value='form2'>Form 2</option>
          </select>
        </form>
        <button onClick={() => setCountER(cnt + 1)}>+</button>
      </header>
    </div>
  );
}

export default Form;
